			$scope.hideBuildDashboardContainer =  false;
			$scope.hideBuildDashboardTab  = false;
			$scope.hideNISPDashboardContainer =  false;
			$scope.hideNISPDashboardTab  = false;
			$scope.hideNISDashboardContainer =  false;
			$scope.hideNISDashboardTab  = false;
			$scope.hideClientDeploymentDashboardContainer =  false;
