sp_widget.script:
(function(){
	var gr = $sp.getInstanceRecord();
	//data.href = $sp.getMenuHREF(gr);
//	data.target = options.target || "";
	data.gtcadmin=gs.hasRole('x_tnmu2_gtc_mgmt_s.GTC Admin_role');
	data.content=gs.hasRole('x_tnmu2_gtc_mgmt_s.Content Design User');
	data.build=gs.hasRole('x_tnmu2_gtc_mgmt_s.IDC BUILD team users_role');
	data.nisp = gs.hasRole('x_tnmu2_gtc_mgmt_s.NISP Admin user');
	data.nis=gs.hasRole('x_tnmu2_gtc_mgmt_s.NIS User');
	data.clientdlp=gs.hasRole('x_tnmu2_gtc_mgmt_s.Client Deployment User');
	data.IVTaem=gs.hasRole('x_tnmu2_gtc_mgmt_s.IV Team users');
	data.nispUser=gs.hasRole('x_tnmu2_gtc_mgmt_s.NISP User');
	data.LocalCountry=gs.hasRole('x_tnmu2_gtc_mgmt_s.Local_Country_Users');
	data.PMO=gs.hasRole('x_tnmu2_gtc_mgmt_s.PMO Users');

})();
