function onLoad() 
	{
 		var cli_param=g_form.getValue('client');
		var cat_param=g_form.getValue('category');
		var pro_param=g_form.getValue('project');

		if(cli_param == '' && cat_param == '' && pro_param == '')
			{
				var cli1 = new GlideAjax('OnloadDynamicallySettingChoice');
				cli1.addParam('sysparm_name', 'client');
				cli1.getXML(clnt2);
				function clnt2(response) 
					{
						var answer = response.responseXML.documentElement.getAttribute("answer");
						answer = answer.evalJSON(); 
						var r = []; 
						o:for( var i=0 ; i < answer.length; i++) 
							{ 	
								for(var x = 0; x < r.length; x++)
									{
										if(r[x]==answer[i])
											{
												 continue o;
											}
									}
								r[r.length] = answer[i];
								var abc= answer[i];
								g_form.addOption('client',abc,abc); 
							}
					}
				g_form.setReadOnly('project',true);
				g_form.setReadOnly('category',true);

	  }
		if(cli_param != '' && cat_param != '' && pro_param != '')
			{
				g_form.setReadOnly('project',true);
				g_form.setReadOnly('category',true);
			}
	}
