sys_script_include.script:
var OnloadDynamicallySettingChoice = Class.create();
OnloadDynamicallySettingChoice.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	client: function()
		{
			var userRecord = new GlideRecord('x_tnmu2_gtc_mgmt_s_tbl_content_design_unique_ref');
			userRecord.query();
			var customArray = [];
			while (userRecord.next()) 
			{ 
				customArray.push(userRecord.getValue('client')); 
			 }
			var data = JSON.stringify(customArray);
			return data;
		},
	project: function() 
		{   
			var cli_param = this.getParameter('sysparm_cli_param');
			var userRecord = new GlideRecord('x_tnmu2_gtc_mgmt_s_tbl_content_design_unique_ref');
			userRecord.addQuery('client',cli_param);
			userRecord.query();
			var customArray = [];
			while (userRecord.next()) 
			{ 
				customArray.push(userRecord.getValue('project_name')); 
			 }

			var data = JSON.stringify(customArray);
			return data;

		},
	category: function() 
		{
			var cli_param = this.getParameter('sysparm_cli_param');
			var pro_param = this.getParameter('sysparm_pro_param');	
			var userRecord = new GlideRecord('x_tnmu2_gtc_mgmt_s_tbl_content_design_unique_ref');
			userRecord.addQuery('client',cli_param);
			userRecord.addQuery('project_name',pro_param);
			userRecord.query();
			var customArray = [];
			while (userRecord.next()) 
			{ 
				customArray.push(userRecord.getValue('category')); 
			 }
			var data = JSON.stringify(customArray);
			return data;
		},

    type: 'OnloadDynamicallySettingChoice'
});
