sys_script_include.script:
var Loading_user_Manager_Deetails = Class.create();
Loading_user_Manager_Deetails.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {


	UserDetails: function()
		{
			var SysId = this.getParameter('sysparm_sysid');
			var userRecord = new GlideRecord('sys_user');
			userRecord.addQuery('sys_id',SysId);
			userRecord.query();
			var customArray = [];
			var mgr_sys_id;
			if(userRecord.next()) 
			{ 
				if(userRecord.getValue('user_name'))
				customArray.push(userRecord.getValue('user_name'));
				else
					customArray.push(' ');

				if(userRecord.getValue('email'))
				customArray.push(userRecord.getValue('email'));
				else
					customArray.push(' ');

				if(userRecord.getValue('manager'))
				customArray.push(userRecord.getValue('manager'));
				else
					customArray.push('');

				mgr_sys_id=userRecord.getValue('manager');

			 }
			var MgrRecord = new GlideRecord('sys_user');
			MgrRecord.addQuery('sys_id',mgr_sys_id);
			MgrRecord.query();
			var count=0;
			if(MgrRecord.next()) 
			{ 
				if(MgrRecord.getValue('email'))
				customArray.push(MgrRecord.getValue('user_name'));

				if(userRecord.getValue('email'))
				customArray.push(MgrRecord.getValue('email'));

				count=count+1;


			 }
			if(count==0)
				{
				customArray.push('');
				customArray.push('');
				}

			var data = JSON.stringify(customArray);
			return data;
		},



	mgrDetails: function()
		{
			var SysId = this.getParameter('sysparm_sysid');
			var userRecord = new GlideRecord('sys_user');
			userRecord.addQuery('sys_id',SysId);
			userRecord.query();
			var customArray = [];
			if(userRecord.next()) 
			{ 
				if(userRecord.getValue('user_name'))
				customArray.push(userRecord.getValue('user_name'));
				else
					customArray.push(' ');

				if(userRecord.getValue('email'))
				customArray.push(userRecord.getValue('email'));
				else
					customArray.push(' ');


			 }

			var data = JSON.stringify(customArray);
			return data;
		},




    type: 'Loading_user_Manager_Deetails'
});
