sys_script_include.script:
var Loading_choice_list_from_loc = Class.create();
Loading_choice_list_from_loc.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	LocDetails: function()
		{
			var locRecord = new GlideRecord('cmn_location');
			locRecord.orderByDesc('city');
			locRecord.addActiveQuery();
			locRecord.query();

			var customArray = [];
			var count1=0;
			while (locRecord.next()) 
			{ 


				if(count1 == 0 || (locRecord.getValue('city')!='' && locRecord.getValue('city') != customArray[count1-1]  ))
					{
				customArray.push(locRecord.getValue('city'));
				count1=count1+1;
					}

			 }
			var data = JSON.stringify(customArray);
			return data;
		},




    type: 'Loading_choice_list_from_loc'
});
